    private void jButton3ActionPerformed(java.awt.event.ActionEvent evt) {   
    	try {
			double speed = Double.parseDouble(jTextField1.getText().replaceAll("X", ""));
			jTextField1.setText(String.valueOf(speed/2) + "X");
		}
		catch(Exception e) {
		}
    }                                      

