    public static double getSpeed() {
    	if (jTextField1.getText().contains("X")) {
    		try {
    			return Double.parseDouble(jTextField1.getText().replaceAll("X", ""));
    		}
    		catch(Exception e) {
    			return 1;
    		}
    	}
    	
    	return 1;
    	
    }
    
    public static void setSpeed(double speed) {
    	jTextField1.setText(String.valueOf(speed) + "X");
    }
    
    public static void updateTimeElapsed(double time) {
    	jTextField2.setText(new DecimalFormat("#.##").format(time) + " days");
    }
    
